INSERT INTO `ingredient` (`id`,`nom`, `descripcio` ) VALUES ('wyvern_hide', 'Wyvern hide', 'Wyvern hide is an alchemy ingredient in The Witcher 3: Wild Hunt but is not needed for anything in particular. It can be dismantled into a Monster carapace and Chitinous shell.');
