INSERT INTO `ingredient` (`id`,`nom`, `descripcio` ) VALUES ('aether', 'Aether', 'Aether, indicated in purple, is a basic alchemical substance. It is found in many ingredients, including herbs, minerals and monster parts. This category contains all ingredients with aether as their primary substance.');
